        public class FinalFloor
        {
            private const int ActualFloor = 74;

            [Theory]
            [InlineData("(())", 0)]
            [InlineData("()()", 0)]
            [InlineData("(((", 3)]
            [InlineData("(()(()(", 3)]
            [InlineData("))(((((", 3)]
            [InlineData("())", -1)]
            [InlineData("))(", -1)]
            [InlineData(")))", -3)]
            [InlineData(")())())", -3)]
            public void FindsCorrectFloorForExamples(string descriptor, int expectedFloor)
            {
                var actual = Day1.FinalFloor(descriptor);
                Assert.Equal(expectedFloor, actual);
            }

            [Fact]
            public void Actual()
            {
                var floor = Day1.FinalFloor(testData);
                Assert.Equal(ActualFloor, floor);
            }
        }

        public class StepToEnterBasement
        {
            private const int ActualStep = 1795;

            [Fact]
            public void ThrowsIfNeverEntersBasement()
            {
                Assert.Throws<InvalidOperationException>(() => Day1.StepToEnterBasement("("));
            }

            [Theory]
            [InlineData(")", 1)]
            [InlineData("()())", 5)]
            public void FindsCorrectStepForExamples(string descriptor, int expectedStep)
            {
                var actual = Day1.StepToEnterBasement(descriptor);
                Assert.Equal(expectedStep, actual);
            }

            [Fact]
            public void Actual()
            {
                var step = Day1.StepToEnterBasement(testData);
                Assert.Equal(ActualStep, step);
            }
        }
